script:
frappe.ui.form.on('Salary Structure Assignment',  {
    refresh: function(frm) {
		// your code here
		frm.doc.ht_gross_salary = frm.doc.base + frm.doc.ht_housing_allowance + frm.doc.ht_food_allowance + frm.doc.ht_transportation_allowance;
		frm.doc.ht_maximum_monthly_deduction=frm.doc.base*0.40;
	}
});

frappe.ui.form.on('Additional Salary', {
	setup(frm) {
		// your code here
		cur_frm.set_query("ht_ssa", function(doc) {
		    return {
		        filters: {
		            "employee": cur_frm.doc.employee
		        }
		    };
		}
		);

	},
	employee(frm){
		    //this will fetch and set the most recent assignment.
        frappe.db.get_link_options('Salary Structure Assignment', '',
            {
				employee: cur_frm.doc.employee
			}
		).then(all_ssa =>{
		    frm.set_value("ht_ssa", all_ssa[0].value);
		});
	},

	validate(frm){
	    if(frm.doc.salary_component=="Over Time"){
	        frm.doc.ht_basic_salary_per_hour=frm.doc.ht_ind_basic_salary/240;
	        frm.doc.amount=frm.doc.ht_overtime_hours*frm.doc.ht_overtime_rate_per_hour*frm.doc.ht_basic_salary_per_hour;
	    }
	}

}



);
